package com.adedom.dodoung

import io.ktor.application.*
import io.ktor.features.*
import io.ktor.gson.*
import io.ktor.response.*
import io.ktor.routing.*
import java.text.SimpleDateFormat
import java.util.*

fun main(args: Array<String>): Unit = io.ktor.server.netty.EngineMain.main(args)

fun Application.module() {
    install(DefaultHeaders)
    install(CallLogging)

    install(ContentNegotiation) {
        gson {
        }
    }

    install(Routing) {
        get("/api/adedom/forecast") {
            val firstName = call.parameters["firstName"]
            val response = ForecastResponse()

            val message: String = when {
                firstName.isNullOrBlank() -> "Please enter your name"
                sumTotalFirstName(firstName) == 0 -> "Please enter a valid name"
                else -> {
                    val numTotalFirstName = sumTotalFirstName(firstName)
                    val numTotalDay = Date().date + Date().day.plus(1)
                    val numTotalForecast = numTotalFirstName + numTotalDay
                    val sdf = SimpleDateFormat("EEEE, dd MMMM yyyy HH:mm")
                    val dateTime = sdf.format(System.currentTimeMillis())

                    val forecast = Forecast(
                        firstName = firstName,
                        numTotalFirstName = numTotalFirstName,
                        numTotalDay = numTotalDay,
                        numTotalForecast = numTotalForecast,
                        forecastMessage = forecastMessage(numTotalForecast),
                        dateTime = dateTime,
                    )
                    response.forecast = forecast
                    response.success = true
                    "Forecast success"
                }
            }

            response.message = message
            call.respond(response)
        }
    }
}

data class ForecastResponse(
    var success: Boolean = false,
    var message: String? = null,
    var forecast: Forecast? = null,
)

data class Forecast(
    val firstName: String? = null,
    val numTotalFirstName: Int? = null,
    val numTotalDay: Int? = null,
    val numTotalForecast: Int? = null,
    val forecastMessage: String? = null,
    val dateTime: String? = null,
)

fun sumTotalFirstName(firstName: String): Int {
    val data = mapOf(
        "อ" to 1, "ั" to 0, "์" to 0, "่" to 0, "้" to 0, "๊" to 0, "๋" to 0, "ๆ" to 0, "็" to 0, "ะ" to 1, "า" to 1,
        "ิ" to 1, "ี" to 1, "ึ" to 1, "ื" to 1, "ุ" to 1, "ู" to 1, "เ" to 1, "แ" to 2, "ฤ" to 1, "ฦ" to 1, "ำ" to 1,
        "ไ" to 1, "โ" to 1, "ใ" to 1, "ๆ" to 1, "ฤ" to 1, "ฦ" to 1, "ก" to 2, "ข" to 2, "ฃ" to 2, "ค" to 2,
        "ฅ" to 2, "ฆ" to 2, "ง" to 2, "จ" to 3, "ฉ" to 3, "ช" to 3, "ซ" to 3, "ฌ" to 3, "ญ" to 3, "ฎ" to 4,
        "ฏ" to 4, "ฐ" to 4, "ฑ" to 4, "ฒ" to 4, "ณ" to 4, "ด" to 7, "ต" to 7, "ถ" to 7, "ท" to 7, "ธ" to 7,
        "น" to 7, "บ" to 5, "ป" to 5, "ผ" to 5, "ฝ" to 5, "พ" to 5, "ฟ" to 5, "ภ" to 5, "ม" to 5, "ย" to 8,
        "ร" to 8, "ล" to 8, "ว" to 8, "ศ" to 6, "ษ" to 6, "ส" to 6, "ห" to 6, "ฬ" to 6, "ฮ" to 6,
    )

    var sum = 0
    firstName.forEachIndexed { index, _ ->
        sum += data[firstName[index].toString()] ?: 0
    }
    return sum
}

